import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import React, {lazy} from 'react';

import translationEn from '../languages/en/translationEn.json'
import translationSv from '../languages/sv/translationSv.json'
import translationDe from '../languages/de/translationDe.json'

// const translationEn = lazy(() => import('../languages/en/translationEn.json'));
// const translationSv = lazy(() => import('../languages/sv/translationSv.json'));
// const translationDe = lazy(() => import('../languages/de/translationDe.json'));

const resources = {
  en: {
    translation: translationEn
  },
  sv: {
    translation: translationSv
  },
  de: {
    translation: translationDe
  }
}

i18n

  .use(initReactI18next)
  .init({
   
    lng: 'en',
    debug: true,
    fallbackLng: 'en',
    detection: {
    order: ['queryString', 'cookie'],
    cache: ['cookie']
    },
    interpolation: {
      escapeValue: false,
    },
    resources
});

export default i18n;
